#include <iostream>#include <queue>using namespace std;class MyStack {    queue<int> *q;public:    MyStack() {        q = new queue<int>;    }    void push(int x) {	q->push(x);    }    int pop() {        for (int i = 1; i < q->size(); i++) {		q->push(q->front()); q->pop();	}	int v = q->front();	q->pop();        return v;    }};int main() {	MyStack stack;	int choice, value;	while (1) {		cout << "1. Push\n2. Pop\n";		cout << "Other. Exit\nEnter your Choice: ";		cin >> choice;		switch (choice) {			case 1: cout << "Enter a Value: "; cin >> value;				stack.push(value); break;			case 2: value = stack.pop();				cout << "Popped Value is: " << value << endl; break;			default: return 0;		}	}	return 0;}